mapper.select:
SELECT tb_id, transfer_status, tb_title, tb_content, tb_create_time, tb_update_time, room_latitude, room_longitude, room_address, room_floor, room_type, contract_type, contract_end_time, transferer_id, transferee_id, like_count, approving_status, dc.dongCode, sidoName, gugunName, dongName , dc.sidoName,dc.gugunName,dc.dongName FROM transfer_boards as tb JOIN regions as dc ON tb.dongCode = dc.dongCode JOIN users transferer ON tb.transferer_id = transferer.user_id LEFT OUTER JOIN users transferee ON tb.transferee_id = transferee.user_id WHERE transfer_status = 1 WHERE transfer_status = 1 or transfer_status = 0 AND approving_status = #{approvingStatus}
AND (transferer.user_email = #{userEmail} OR transferee.user_email = #{userEmail})
AND tb_id = #{transferBoardId} AND dc.dongCode = #{dongCode}
ORDER BY tb.like_count DESC, tb.tb_create_time DESC
ORDER BY tb.tb_create_time DESC
LIMIT #{numOfRows}

mapper.update:
UPDATE transfer_boards
SET transferee_id = #{transfereeId}
WHERE tb_id = #{transferBoardId}

UPDATE transfer_boards
SET transfer_status = 1
WHERE tb_id = #{transferBoardId}
