mapper.select:
SELECT user_id, user_name, user_email, user_password, user_address
FROM users
WHERE user_id = #{userId}

SELECT user_id, user_name, user_email, user_password, user_address
FROM users
WHERE user_email = #{userEmail}

SELECT user_roles.user_id, user_roles.role_id, role.role_name
FROM user_roles JOIN role ON user_roles.role_id = role.role_id
WHERE user_id = #{userId}

SELECT role_id, role_name
FROM roles
WHERE role_name = #{roleName}
