mapper.select:
SELECT user_id, user_name, user_email, user_password, user_address
FROM user
WHERE user_id = #{userId}

SELECT user_id, user_name, user_email, user_password, user_address
FROM user
WHERE user_email = #{userEmail}

mapper.update:
UPDATE user
SET user_password = #{iserPassword},
    user_address = #{userAddress}
WHERE user_id = #{userId}

mapper.select:
SELECT user_role.user_id, user_role.role_id, role.role_name
FROM user_role JOIN role ON user_role.role_id = role.role_id
WHERE user_id = #{userId}

SELECT role_id, role_name
FROM role
WHERE role_name = #{roleName}
